mapper.select:
select  ri.id, ri.category_id, ri.brand_id, ri.price, ri.deposit_id, ri.pay_id, ri.deposite_amount, ri.pay_amount, ri.total_amount, ri.water_bill, ri.electricity_bill, ri.house_type_id,square, ri.floor, ri.towards, ri.elevator, ri.furnish, ri.home_page_image, ri.introduction, ri.address_id, ri.room_no,ri.address_detail, ri.facilities, ri.labels, ri.favorite_count, ri.browse_count, ri.status, ri.deleted, ri.weights, ri.sort, ri.create_by, ri.create_time, ri.update_by, ri.update_time,
        rb.`name` brand_name, rc.`name` category_name, rht.`name` house_type_name, rd.`name` deposit_name, rp.`name` pay_name
from residence_info ri
left join residence_brand rb on ri.brand_id = rb.id
left join residence_category rc on ri.category_id = rc.id
left join residence_house_type rht on ri.house_type_id = rht.id
left join residence_deposit rd on ri.deposit_id = rd.id
left join residence_pay rp on ri.pay_id = rp.id

     and ri.category_id = #{id}
     and ri.category_id = #{categoryId}
     and ri.brand_id = #{brandId}
     and ri.price = #{price}
     and ri.price >= #{params.minPrice}
     and ri.price <= #{params.maxPrice}
     and ri.water_bill = #{waterBill}
     and ri.electricity_bill = #{electricityBill}
     and ri.house_type_id = #{houseTypeId}
     and ri.square = #{square}
     and ri.floor = #{floor}
     and ri.towards = #{towards}
     and ri.elevator = #{elevator}
     and ri.furnish = #{furnish}
     and ri.address_id = #{addressId}
     and ri.room_no like concat('%', #{roomNo}, '%')
     and ri.address_detail = #{addressDetail}
     and ri.status = #{status}
     and ri.create_time between #{params.beginCreateTime} and #{params.endCreateTime}



        order by ${params.sortSql}


        order by sort desc, ri.create_time desc

mapper.update:
update residence_info
category_id = #{categoryId}, brand_id = #{brandId}, price = #{price}, deposit_id = #{depositId}, pay_id = #{payId}, deposite_amount = #{depositeAmount}, pay_amount = #{payAmount}, total_amount = #{totalAmount}, water_bill = #{waterBill}, electricity_bill = #{electricityBill}, house_type_id = #{houseTypeId}, square = #{square}, floor = #{floor}, floor = #{towards}, elevator = #{elevator}, furnish = #{furnish}, home_page_image = #{homePageImage}, introduction = #{introduction}, address_id = #{addressId}, room_no = #{roomNo}, address_detail = #{addressDetail}, facilities = #{facilities}, labels = #{labels}, favorite_count = #{favoriteCount}, browse_count = #{browseCount}, status = #{status}, deleted = #{deleted}, weights = #{weights}, sort = #{sort}, update_by = #{updateBy}, update_time = #{updateTime},
where id = #{id}
